mapper.select:
SELECT MR.*, TA.TA_NAME, MD.MD_DATE, MD.MD_TIME, MD.MD_ADDRESS FROM MATCH_RESULT_INFO MR INNER JOIN TEAM_INFO TA ON TA.TA_NUM = MR.TA_HOME_NUM INNER JOIN MATCH_DEAL_INFO MD ON MD.MD_NUM = MR.MD_NUM
(TA_HOME_NUM = #{taNum} OR TA_AWAY_NUM = #{taNum})
TA_AWAY_NUM = #{taNum}
TA_HOME_NUM = #{taNum}
AND NOT MR_REQUEST_STATUS = '3'
ORDER BY MD_DATE DESC, MD_TIME DESC

SELECT MR.*, TA.TA_NAME, MD.MD_DATE, MD.MD_TIME, MD.MD_ADDRESS FROM MATCH_RESULT_INFO MR INNER JOIN TEAM_INFO TA ON TA.TA_NUM = MR.TA_HOME_NUM INNER JOIN MATCH_DEAL_INFO MD ON MD.MD_NUM = MR.MD_NUM
(TA_HOME_NUM = #{taNum} OR TA_AWAY_NUM = #{taNum})
TA_AWAY_NUM = #{taNum}
TA_HOME_NUM = #{taNum}
AND MR_REQUEST_STATUS = '3'
ORDER BY MD_DATE DESC, MD_TIME DESC
