mapper.select:
SELECT * FROM TEAM_INFO


		TA_TYPE = #{taType}


		AND TA_NUM = #{taNum}

	AND TA_ACTIVE_STATUS = 0

mapper.insert:
INSERT INTO TEAM_INFO(
UI_NUM, TA_NAME, TA_BOUNDARY_SIDO,
TA_BOUNDARY_SIGUNGU,
TA_DESC,TA_TYPE,
TA_FILE_PATH,
TA_FILE_NAME
)
VALUES(
#{uiNum}, #{taName},
#{taBoundarySido},#{taBoundarySigungu},
#{taDesc},#{taType},#{taFilePath},#{taFileName}
);

mapper.select:
SELECT *
FROM TEAM_INFO


		TA_TYPE = #{taType}


		AND TA_BOUNDARY_SIDO = #{taBoundarySido}


		AND TA_POINT > #{taPoint}


		AND TA_NAME = #{taName}

	AND TA_ACTIVE_STATUS = 0

ORDER BY TA_POINT DESC
LIMIT 100;

SELECT * FROM TEAM_INFO
WHERE TA_NUM = #{taNum}
AND TA_TYPE = #{taType}
AND TA_ACTIVE_STATUS = 0

SELECT * FROM TEAM_INFO
WHERE TA_TYPE = #{taType}
ORDER BY
TA_POINT DESC;

SELECT * FROM TEAM_INFO


		UI_NUM = #{uiNum}


		AND TA_NUM = #{taNum}

mapper.update:
UPDATE TEAM_INFO


		TA_FILE_PATH = #{taFilePath},


		TA_FILE_NAME = #{taFileName},


		TA_NAME = #{taName},


		TA_DESC = #{taDesc},


		TA_STATUS = #{taStatus}


		TA_ACTIVE_STATUS = #{taActiveStatus}


WHERE TA_NUM = #{taNum};
