mapper.select:
SELECT * FROM USER_INFO
WHERE UI_NUM = #{uiNum}
AND UI_ACTIVE_STATUS = 0

mapper.update:
UPDATE USER_INFO


		UI_PWD =#{uiPwd},


		UI_EMAIL = #{uiEmail},


		UI_BIRTH = #{uiBirth},


		UI_ADDRESS = #{uiAddress},


		UI_PHONE_NUM= #{uiPhoneNum}


		UI_ACTIVE_STATUS= #{uiActiveStatus}


WHERE UI_NUM = #{uiNum}
